package com.example.tmpl.exception;import java.util.HashMap;import java.util.Map;public class TmplExceptionMsg {    private static final Map<String, Object> exceptionMsgs = new HashMap<>();    static {        exceptionMsgs.put("E001", "Cannot convert null or \"\" object to string");    }    public static String getExceptionMsg(String exceptionCode) {        return (String) exceptionMsgs.getOrDefault(exceptionCode, exceptionCode + " is undefined exception code");    }}