package com.example.tmpl.svc;import com.example.tmpl.dao.MemberDao;import com.example.util.Converter;import com.example.util.Crypto;import java.util.HashMap;import java.util.Map;import org.springframework.stereotype.Service;@Servicepublic class MemberSvc {    private final MemberDao memberDao;    public MemberSvc(MemberDao memberDao) {        this.memberDao = memberDao;    }    public void insert(Map<String, Object> req) {        Map<String, Object> svcMap = new HashMap<>();        svcMap.put("i_mb_id", Converter.toStr(req.get("i_mb_id")));        svcMap.put("i_mb_nickname", Converter.toStr(req.get("i_mb_nickname")));        svcMap.put("i_mb_email", Converter.toStr(req.get("i_mb_email")));        svcMap.put("i_mb_password", Crypto.encode(Converter.toStr(req.get("i_mb_password"))));        svcMap.put("i_mb_role", Converter.toStr(req.get("i_mb_role")));        memberDao.insert(svcMap);    }    public Map<String, Object> selById(Map<String, Object> params) {        Map<String, Object> svcMap = new HashMap<>();        svcMap.put("w_mb_id", Converter.toStr(params.get("w_mb_id")));        return memberDao.selById(svcMap);    }    public void upById(Map<String, Object> req) {        Map<String, Object> svcMap = new HashMap<>();        svcMap.put("u_mb_nickname", Converter.toStr(req.get("u_mb_nickname")));        svcMap.put("u_mb_email", Converter.toStr(req.get("u_mb_email")));        svcMap.put("u_mb_password", Crypto.encode(Converter.toStr(req.get("u_mb_password"))));        svcMap.put("u_mb_role", Converter.toStr(req.get("u_mb_role")));        svcMap.put("w_mb_id", Converter.toStr(req.get("w_mb_id")));        memberDao.upById(svcMap);    }    public void delById(Map<String, Object> params) {        Map<String, Object> svcMap = new HashMap<>();        svcMap.put("w_mb_id", Converter.toStr(params.get("w_mb_id")));        memberDao.delById(svcMap);    }}